    private void btnDiscardProductActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDiscardProductActionPerformed

        clearAll();
    }//GEN-LAST:event_btnDiscardProductActionPerformed

    private void btnNewProductActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNewProductActionPerformed

        if (txtProductName.getText().equals("") || txtSalePrice.getText().equals("")
                || txtSizeProduct.getText().equals("") || txtProductDescription.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "Todos los campos son obligatorios.");
        } else {
            String message;
            String productName = txtProductName.getText();
            String salePrice = txtSalePrice.getText();
            String productSize = txtSizeProduct.getText();
            String description = txtProductDescription.getText();

            message = productDAO.createProduct(productName, salePrice, productSize, description);
            JOptionPane.showMessageDialog(this, message);
            clearAll();
        }
    }//GEN-LAST:event_btnNewProductActionPerformed

    private void txtSalePriceKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtSalePriceKeyTyped
        char c = evt.getKeyChar();

        if (c < '0' || c > '9') {
            evt.consume();
            JOptionPane.showMessageDialog(this, "Ingrese numeros");

        }
    }//GEN-LAST:event_txtSalePriceKeyTyped

    private void txtSizeProductKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtSizeProductKeyTyped
        char c = evt.getKeyChar();

        if (c < '0' || c > '9') {
            evt.consume();
            JOptionPane.showMessageDialog(this, "Ingrese numeros");

        }
    }//GEN-LAST:event_txtSizeProductKeyTyped

    public void clearAll() {
        txtProductName.setText("");
        txtSalePrice.setText("");
        txtSizeProduct.setText("");
        txtProductDescription.setText("");

        JOptionPane.showMessageDialog(this, "El producto ha sido descartado.");
    }

